##Displays a select list of Categories from DSS API pull
#set($content = $dotcontent.find("$!{identifier}"))
#set($categoryRef = $content.categoryName)
##provides select list to choose a category from the api return
<select id="categoryName-select" onchange="onCategoryNameChange()" multiple="multiple">
  ##pulls a raw parent category list from dss api
  #set($catRaw = $json.fetch("https://dssdias-sit.intershop.cloud/INTERSHOP/rest/WFS/inSPIRED-inTRONICS_Business-Site/-;loc=en_US;cur=USD/categories"))
    #foreach($category in $catRaw.elements)
      #if($velocityCount==1)
        <option value=""></option>
      #end
      <option value="$!{category.categoryRef}" #if($categoryRef == $category.categoryRef)selected#end>$!{category.name}</option>
    #end
</select>
<script>
    function onCategoryNameChange() {
      setValue();
      setChildCategories();
    }

    function setValue(){
      //Gets the value of the select field
      const fieldValue = document.getElementById("categoryName-select").value;
      
      // Sets the custom field value in this case the field was called Parent Category
      document.getElementById("categoryName").value = fieldValue;
      
      // Create the event
      const event = new Event('categories-changes', {
        bubbles: true,
        cancelable: true,
      });
      
      // Dispatch the event
      document.dispatchEvent(event);
    }

    // Initialize the value when the page loads
    window.addEventListener('load', setValue);
</script>
